ALTER TABLE xlite_modules CHANGE version version varchar(12) NOT NULL DEFAULT '0';
CREATE TABLE xlite_giftcerts ( 
    gcid varchar(16) NOT NULL default '', 
    profile_id int(11) NOT NULL default 0, 
    purchaser varchar(64) NOT NULL default '', 
    recipient varchar(64) NOT NULL default '', 
    send_via char(1) NOT NULL default 'E', 
    recipient_email varchar(64) NOT NULL default '', 
    recipient_firstname varchar(128) NOT NULL default '', 
    recipient_lastname varchar(128) NOT NULL default '', 
    recipient_address varchar(64) NOT NULL default '', 
    recipient_city varchar(64) NOT NULL default '', 
    recipient_state varchar(32) NOT NULL default '', 
	recipient_custom_state varchar(64) NOT NULL default '',
    recipient_zipcode varchar(32) NOT NULL default '', 
    recipient_country char(2) NOT NULL default '', 
    recipient_phone varchar(32) NOT NULL default '', 
    message text NOT NULL, 
    amount decimal(12,2) NOT NULL default '0.00', 
    debit decimal(12,2) NOT NULL default '0.00', 
    status char(1) NOT NULL default 'P', 
    add_date int(11) NOT NULL default '0',
    expiration_date int(11) NOT NULL default '0',
    exp_email_sent int(1) NOT NULL default 0,
    greetings varchar(100) NOT NULL default '',
    farewell varchar(100) NOT NULL default '',
    border varchar(32) NOT NULL default '',
    ecard_id int, 
    PRIMARY KEY  (gcid), KEY add_date (add_date)
) TYPE=MyISAM;
ALTER TABLE xlite_giftcerts ADD expiration_date int(11) NOT NULL default '0' AFTER add_date;
ALTER TABLE xlite_giftcerts ADD profile_id int(11) NOT NULL default '0' AFTER gcid;
ALTER TABLE xlite_giftcerts ADD exp_email_sent int(1) NOT NULL default 0 AFTER expiration_date;
ALTER TABLE xlite_giftcerts ADD recipient_custom_state varchar(64) NOT NULL default '' AFTER recipient_state;

CREATE TABLE xlite_ecards (
    ecard_id int not null primary key auto_increment,
    thumbnail mediumblob,
    thumbnail_source char(1) NOT NULL default 'D',
    thumbnail_type varchar(32) NOT NULL default '',
    image mediumblob,
    image_source char(1) NOT NULL default 'D',
    image_type varchar(32) NOT NULL default '',
    template varchar(40),
    order_by int not null default 0,
    enabled int not null default 1
) TYPE=MyISAM;
ALTER TABLE xlite_order_items ADD gcid varchar(16);
ALTER TABLE xlite_orders ADD payedByGC decimal(12,2);
ALTER TABLE xlite_orders ADD gcid varchar(16);

